#!/usr/bin/env bash [[ -f "${TM_SUPPORT_PATH}/lib/bash_init.sh" ]] && . "${TM_SUPPORT_PATH}/lib/bash_init.sh"
export CommitWindow="$TM_SCM_COMMIT_WINDOW"
{ if [[ -z $TM_SELECTED_FILES ]] then export WorkPath="${TM_PROJECT_DIRECTORY:-$TM_DIRECTORY}" export TM_SELECTED_FILES="'"${WorkPath//\'/\'\\\'\'}"'" "${TM_SVN:-svn}" status "$WorkPath" 2>&1| ruby -KU -- "${TM_BUNDLE_SUPPORT}/format_status.rb" "--status" else eval "${TM_SVN:-svn}" status "$TM_SELECTED_FILES" 2>&1 |ruby -KU -- "${TM_BUNDLE_SUPPORT}/format_status.rb" "--status" fi; }
